#include "common.h"
#include "pattern.h"

int pattern_squares[PATTERNS][SQUARES][CELLS];

void init_pattern_squares() {
    for (int p = 0; p < PATTERNS; p++) {
        Pattern *pattern = patterns + p;
        for (int sq = 0; sq < SQUARES; sq++) {
            for (int index = 0; index < CELLS; index++) {
                pattern_squares[p][sq][index] = -1;
            }
            int index = 0;
            int x = sq % WIDTH;
            int y = sq / WIDTH;
            for (int j = 0; j < 7; j++) {
                for (int i = 0; i < 7; i++) {
                    int t = pattern->data[j][i];
                    if (t != FILLED) {
                        continue;
                    }
                    int ax = x - i + 1;
                    int ay = y - j + 1;
                    if (ax < 0 || ay < 0 || ax >= WIDTH || ay >= HEIGHT) {
                        continue;
                    }
                    int bx = ax + pattern->width - 1;
                    int by = ay + pattern->height - 1;
                    if (bx < 0 || by < 0 || bx >= WIDTH || by >= HEIGHT) {
                        continue;
                    }
                    pattern_squares[p][sq][index++] = SQ(ax, ay);
                }
            }
        }
    }
}

